jQuery.validator.addMethod("isMobile", function(value, element) {
    var length = value.length;
    var mobile = /^(13[0-9]{9})|(18[0-9]{9})|(14[0-9]{9})|(17[0-9]{9})|(15[0-9]{9})$/;
    return this.optional(element) || (length == 11 && mobile.test(value));
}, "<i class='fa fa-times-circle'></i> 请正确填写您的手机号码");
$.validator.setDefaults({
	highlight: function(e) {
		$(e).closest(".form-group").removeClass("has-success").addClass("has-error")
	},
	success: function(e) {
		e.closest(".form-group").removeClass("has-error").addClass("has-success")
	},
	errorElement: "span",
	errorPlacement: function(e, r) {
		e.appendTo(r.is(":radio") || r.is(":checkbox") ? r.parent().parent().parent() : r.parent())
	},
	errorClass: "help-block m-b-none",
	validClass: "help-block m-b-none"
}), $().ready(function() {
	$("#commentForm").validate();
	var e = "<i class='fa fa-times-circle'></i> ";
	$("#signupForm").validate({
		rules: {
			firstname: "required",
			lastname: "required",
			username: {
				required: !0,
				minlength: 2
			},
			createid:{
				required: !0,
				minlength: 15,
				maxlength: 18
			},
			bankid:{
				required: !0,
				min: 1
			},
			bankcard:{
				required: !0,
				minlength: 16,
				maxlength: 19
			},
			verify: {
				required:true,
				minlength: 4,
				maxlength: 4
			},
			address: {
				required:true,
				minlength: 4,
				maxlength: 20
			},
			oldpassword: {
				required: !0,
				minlength: 6
			},
			password: {
				required: !0,
				minlength: 6
			},
			passwords: {
				required: !0,
				minlength: 6
			},
			confirm_password: {
				required: !0,
				minlength: 6,
				equalTo: "#password"
			},
			mobile : {
				required : true,
				minlength : 11,
				isMobile : true
			},
			umobile : {
				required : true,
				minlength : 11,
				isMobile : true
			},
			email: {
				required: !0,
				email: !0
			},
			topic: {
				required: "#newsletter:checked",
				minlength: 2
			},
			agree: "required"
		},
		messages: {
			firstname: e + "请输入你的姓",
			lastname: e + "请输入名字",
			username: {
				required: e + "请输入您的用户名",
				minlength: e + "用户名必须两个字符以上"
			},
			bankid:{
				min: e + "必选"
			},
			oldpassword: {
				required: e + "请输入您的原密码",
				minlength: e + "密码必须6个字符以上"
			},
			password: {
				required: e + "请输入您的密码",
				minlength: e + "密码必须6个字符以上"
			},
			passwords: {
				required: e + "请输入您的密码",
				minlength: e + "密码必须6个字符以上"
			},
			confirm_password: {
				required: e + "请再次输入密码",
				minlength: e + "密码必须6个字符以上",
				equalTo: e + "两次输入的密码不一致"
			},
			email: e + "请输入您的E-mail",
			agree: {
				required: e + "必须同意协议后才能注册",
				element: "#agree-error"
			}
		}
	}), $("#username").focus(function() {
		var e = $("#firstname").val(),
			r = $("#lastname").val();
		e && r && !this.value && (this.value = e + "." + r)
	})
});
